const { Client } = require('@notionhq/client');
const notion = new Client({ auth: process.env.NOTION_KEY });
const databaseId = '1f6b061dadf38008852bfdf0b2679cc2';async function fetchDueToday() {
const today = new Date().toISOString().split('T')[0]; // 例: "2025-06-25"
const response = await notion.databases.query({
database_id: databaseId,
page_size: 30,
filter: {
property: '支払予定日',
date: { equals: today }
}
});
return response;
}
